const HUMAN = "you";
const AI = "ai";
const TOTALNUMOFROWS = 6;
const TOTALNUMOFCOLS = 7;
const TOTALNUMOFSQUARES = TOTALNUMOFROWS * TOTALNUMOFCOLS;


class Connect4Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            squares: Array(TOTALNUMOFROWS).fill(Array(TOTALNUMOFCOLS).fill(null)), // i.e. squares[rowIdx][colIdx] is initialized to null and will be either HUMAN or AI when pieces are placed onto board
            isHumanTurn: true,
            mostRecentSelectionRowIdx : null,
            mostRecentSelectionColIdx: null
        };
        this.handleClick = this.handleClick.bind(this);
        this.findLegalRowIdxWithinColumn = this.findLegalRowIdxWithinColumn.bind(this);
        this.calculateWinner = this.calculateWinner.bind(this);
        this.handleReset = this.handleReset.bind(this);
    }

    handleClick(selectedColIdx) {
        const squares = this.state.squares.slice();
        const legalRowPositionToFillIdx = this.findLegalRowIdxWithinColumn(selectedColIdx);
        if (legalRowPositionToFillIdx !== -1) {
            squares[legalRowPositionToFillIdx, selectedColIdx] = this.state.isHumanTurn ? HUMAN : AI;
            this.setState(prevState => ({
                squares: squares,
                isHumanTurn: !prevState.isHumanTurn,
                mostRecentSelectionRowIdx: legalRowPositionToFillIdx,
                mostRecentSelectionColIdx: selectedColIdx
            }));
        }
    }

    handleReset() {
        this.setState({
            squares: Array(TOTALNUMOFROWS).fill(Array(TOTALNUMOFCOLS).fill(null)), // either HUMAN or AI or null
            isHumanTurn: true,
            mostRecentSelectionRowIdx : null,
            mostRecentSelectionColIdx: null
        });
    }

    findLegalRowIdxWithinColumn(selectedColIdx) {
        /**
         * Returns the row index of a legal square that can be filled.
         * Note the legal square will be within the same column as selectedColIdx.
         * If all the squares within selectedColIdx's column are filled, then this function returns -1.
         */
        const didNotFindLegalRowIdxWithinColumnIdx = -1;
        for (let rowIdx = 0; rowIdx < this.state.squares.length; rowIdx++) {
            if (this.state.squares[rowIdx][selectedColIdx] === null) {
                return rowIdx;
            }
        }
        return didNotFindLegalRowIdxWithinColumnIdx;
    }

    calculateWinner() {
        /**
         * Returns the name of the winner HUMAN or AI. Else returns null.
         */

