const TOTALNUMOFCOLUMNS = 7;
const TOTALNUMOFSQUARES = 42;

function findLegalSquare(board, idx) {
    /**
     * Returns the index of a legal square that can be filled.
     * Note the legal square will be within the same column as idx.
     * If all the squares within idx's column are filled, then this function returns -1.
     */
    const didNotFindLegalSquareIdx = -1;
    let traversingIdx = idx;
    while (traversingIdx - TOTALNUMOFCOLUMNS >= 0) {
        traversingIdx -= TOTALNUMOFCOLUMNS;
    }
    for (traversingIdx; traversingIdx < TOTALNUMOFSQUARES; traversingIdx += TOTALNUMOFCOLUMNS) {
        if (board[traversingIdx] === null) {
            return traversingIdx;
        }
    }
    return didNotFindLegalSquareIdx;
}

function calculateScoreForWindow(windowArr, player, opposingPlayer) {
    /**
     * Returns the score for a given arr, assuming the window is 4
     */

    let score = 0;

    if (windowArr.filter(val => val === player).length === 4) {
        score += 100;
    } else if (windowArr.filter(val => val === player).length === 3 && windowArr.filter(val => val === null).length === 1) {
        score += 5;
    } else if (windowArr.filter(val => val === player).length === 2 && windowArr.filter(val => val === null).length === 2) {
        score += 2;
    }

    if (windowArr.filter(val => val === opposingPlayer).length === 3 && windowArr.filter(val => val === null).length === 1) {
        score -= 15;
    }

    return score;
}

