        for (let i = 0; i < WINDOWLENGTH; i++) {
            const windowArr = newBoard.slice(currRowIdx + i, currRowIdx + i + WINDOWLENGTH);
            score += calculateScoreForWindow(windowArr, player, opposingPlayer);
