        for (let i = currColIdx; i < TOTALNUMOFSQUARES; i += TOTALNUMOFCOLUMNS) {
            verticalArr.push(newBoard[currColIdx + i]);
        }
        for (let i = 0; i < WINDOWLENGTH; i++) {
