    /**
     * Returns the name of the winner HUMAN or AI. Else returns null.
     */

    // Check horizontal
    const frontOfcurrRowIdxIdx = lastSquareIdx - (lastSquareIdx % TOTALNUMOFCOLUMNS);
    let currRowIdxIdx = frontOfcurrRowIdxIdx;
    let howManyConsecutiveCounter = 1; // always starts at 1 because each piece counts as one consecutive piece
    for (currRowIdxIdx; currRowIdxIdx < frontOfcurrRowIdxIdx + TOTALNUMOFCOLUMNS; currRowIdxIdx++) {
        if (howManyConsecutiveCounter === 4) {
            return board[currRowIdxIdx]; // winner has been found, return the winner
        } else if (currRowIdxIdx + 1 < frontOfcurrRowIdxIdx + TOTALNUMOFCOLUMNS && board[currRowIdxIdx] !== null && board[currRowIdxIdx] === board[currRowIdxIdx + 1]) {
            howManyConsecutiveCounter++;
        } else {
            // either the loop ends and there is no winner found here OR the next adjacent piece is a different color
            howManyConsecutiveCounter = 1;
        }
    }

    // Check vertical
    let currColIdx = lastSquareIdx % TOTALNUMOFCOLUMNS;
    const outOfBoundsIdx = TOTALNUMOFSQUARES;
    howManyConsecutiveCounter = 1; // always starts at 1 because each piece counts as one consecutive piece
    for (currColIdx; currColIdx < outOfBoundsIdx ; currColIdx += TOTALNUMOFCOLUMNS) {
        if (howManyConsecutiveCounter === 4) {
            return board[currColIdx]; // winner has been found, return the winner
        } else if (currColIdx + TOTALNUMOFCOLUMNS < outOfBoundsIdx && board[currColIdx] !== null && board[currColIdx] === board[currColIdx + TOTALNUMOFCOLUMNS]) {
            howManyConsecutiveCounter++;
        } else {
            // either the loop ends and there is no winner found here OR the next adjacent piece is a different color
            howManyConsecutiveCounter = 1;
        }
    }

    // Check Diagonal

    // start with BOTTOM-LEFT TO TOP-RIGHT relative to the lastSquareIdx
    // get bottom left squares as indices
    const rightColumnIdx = new Set([6, 13, 20, 27, 34, 41]);
    const leftColumnIdx = new Set([0, 7, 14, 21, 28, 35]);
    const bottomLeftTopRightIdx = [];
    let i = 1;
    while (lastSquareIdx - TOTALNUMOFCOLUMNS * i >= 0) {
        if (lastSquareIdx - TOTALNUMOFCOLUMNS * i - i >= 0 && !rightColumnIdx.has(lastSquareIdx - TOTALNUMOFCOLUMNS * i - i)) {
            bottomLeftTopRightIdx.unshift(lastSquareIdx - TOTALNUMOFCOLUMNS * i - i);
            i++;
        } else {
            break;
        }
    }

    // push the selectedSquareIdx into the bottomLeftTopRightIdx array
    bottomLeftTopRightIdx.push(lastSquareIdx);

