            return board[bottomLeftTopRightIdx[i]]; // winner has been found, return the winner
        } else if (board[bottomLeftTopRightIdx[i]] !== null && board[bottomLeftTopRightIdx[i]] === board[bottomLeftTopRightIdx[i + 1]]) {
