        return null;
    }

    render() {
        const winner = this.calculateWinner();
        return (
            <div className="container">
                <h1 className="d-flex justify-content-center">This is the Connect 4 Project!</h1>
                {winner !== null ? <h2>The winner is: the {winner}</h2> : ""}
                <p>Can you beat the AI?</p>
                <div className="d-flex justify-content-center"><Board squares={this.state.squares} onClick={this.handleClick} /></div>
            </div>
        );
    };
};

export default Connect4Game;
