import Square from './Square';
import React, { Component } from 'react';

class Board extends React.Component {
    renderSquare(i) {
        return (
          <Square
            value={this.props.squares[i]}
            onClick={() => this.props.onClick(i)}
          />
        );
    }

    render() {
        const row5 = [];
        for (let i = 0; i < 7; i++) {
            row5.push(<div key={i}>this.renderSquare(i)</div>);
        }

        const row4 = [];
        for (let i = 7; i < 14; i++) {
            row4.push(this.renderSquare(i));
        }

        const row3 = [];
        for (let i = 14; i < 21; i++) {
            row3.push(this.renderSquare(i));
        }

        const row2 = [];
        for (let i = 21; i < 28; i++) {
            row2.push(this.renderSquare(i));
        }

        const row1 = [];
        for (let i = 28; i < 35; i++) {
            row1.push(this.renderSquare(i));
        }

        const row0 = [];
        for(let i = 35; i < 42; i++) {
            row0.push(this.renderSquare(i));
        }

        return (
            <div>
                <div className="board-row">
                    {row0}
                </div>
                <div className="board-row">
                    {row1}
                </div>
                <div className="board-row">
                    {row2}
                </div>
                <div className="board-row">
                    {row3}
                </div>
                <div className="board-row">
                    {row4}
                </div>
                <div className="board-row">
                    {row5}
                </div>
            </div>
        );
    }
}

export default Board;
