import Board from './connect4/Board';
import React, { Component } from 'react';
import minimax from './connect4/minimax';

// Put any other imports below so that CSS from your
// components takes precedence over default styles.

const HUMAN = "you";
const AI = "ai";

class Connect4Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            squares: Array(42).fill(null), // either HUMAN or AI or null
            isHumanTurn: true,
            selectedSquareIdx: null
        };
        this.handleClick = this.handleClick.bind(this);
        this.findLegalSquare = this.findLegalSquare.bind(this);
        this.calculateWinner = this.calculateWinner.bind(this);
        this.handleReset = this.handleReset.bind(this);
    }

    handleClick(currentSquareIdx) {
        const squares = this.state.squares.slice();
        const legalSquareToFillIdx = this.findLegalSquare(currentSquareIdx);
        if (legalSquareToFillIdx !== -1) {
            squares[legalSquareToFillIdx] = this.state.isHumanTurn ? HUMAN : AI;
            this.setState(prevState => ({
                squares: squares,
                isHumanTurn: !prevState.isHumanTurn,
                selectedSquareIdx: legalSquareToFillIdx
            }));
        }
    }

    handleReset() {
        this.setState = {
            squares: Array(42).fill(null), // either HUMAN or AI or null
            isHumanTurn: true,
            selectedSquareIdx: null
        };
    }

    findLegalSquare(idx) {
        /**
         * Returns the index of a legal square that can be filled. Otherwise, -1.
         */
        const didNotFindLegalSquareIdx = -1;
        let traversingIdx = idx;
        while (traversingIdx - 7 >= 0) {
            traversingIdx -= 7;
        }
        for (traversingIdx; traversingIdx < 42; traversingIdx += 7) {
            if (this.state.squares[traversingIdx] === null) {
                return traversingIdx;
            }
        }
        return didNotFindLegalSquareIdx;
    }

    calculateWinner() {
        /**
         * Returns the name of the winner HUMAN or AI. Else returns null.
         */

        // Check horizontal
        const frontOfCurrRowIdx = this.state.selectedSquareIdx - (this.state.selectedSquareIdx % 7);
        let currRowIdx = frontOfCurrRowIdx;
        let howManyConsecutiveCounter = 1; // always starts at 1 because each piece counts as one consecutive piece
        for (currRowIdx; currRowIdx < frontOfCurrRowIdx + 7; currRowIdx++) {
            if (howManyConsecutiveCounter === 4) {
                return this.state.squares[currRowIdx]; // winner has been found, return the winner
            } else if (currRowIdx + 1 < frontOfCurrRowIdx + 7 && this.state.squares[currRowIdx] !== null && this.state.squares[currRowIdx] === this.state.squares[currRowIdx + 1]) {
                howManyConsecutiveCounter++;
            } else {
                // either the loop ends and there is no winner found here OR the next adjacent piece is a different color
                howManyConsecutiveCounter = 1;
            }
        }

        // Check vertical
        let currColIdx = this.state.selectedSquareIdx % 7;
        const outOfBoundsIdx = 42;
        howManyConsecutiveCounter = 1; // always starts at 1 because each piece counts as one consecutive piece
        for (currColIdx; currColIdx < outOfBoundsIdx ; currColIdx += 7) {
            if (howManyConsecutiveCounter === 4) {
                return this.state.squares[currColIdx]; // winner has been found, return the winner
            } else if (currColIdx + 7 < outOfBoundsIdx && this.state.squares[currColIdx] !== null && this.state.squares[currColIdx] === this.state.squares[currColIdx + 7]) {
                howManyConsecutiveCounter++;
            } else {
                // either the loop ends and there is no winner found here OR the next adjacent piece is a different color
                howManyConsecutiveCounter = 1;
            }
        }

        // Check Diagonal

        // start with BOTTOM-LEFT TO TOP-RIGHT relative to the this.state.selectedSquareIdx
        // get bottom left squares as indices
        const rightColumnIdx = new Set([6, 13, 20, 27, 34, 41]);
        const leftColumnIdx = new Set([0, 7, 14, 21, 28, 35]);
        const bottomLeftTopRightIdx = [];
        let i = 1;
        while (this.state.selectedSquareIdx - 7 * i >= 0) {
            if (this.state.selectedSquareIdx - 7 * i - i >= 0 && !rightColumnIdx.has(this.state.selectedSquareIdx - 7 * i - i)) {
                bottomLeftTopRightIdx.unshift(this.state.selectedSquareIdx - 7 * i - i);
                i++;
            } else {
                break;
            }
        }

        // push the selectedSquareIdx into the bottomLeftTopRightIdx array
        bottomLeftTopRightIdx.push(this.state.selectedSquareIdx);

        // get the top right squares as indices
        i = 1;
        while (this.state.selectedSquareIdx + 7 * i < 42) {
            if (this.state.selectedSquareIdx + 7 * i + i < 42 && !leftColumnIdx.has(this.state.selectedSquareIdx + 7 * i + i)) {
                bottomLeftTopRightIdx.push(this.state.selectedSquareIdx + 7 * i + i);
                i++;
            } else {
                break;
            }
        }

        // check to see if there is a 4-in-a-row from bottom left to top right
        howManyConsecutiveCounter = 1;
        for (let i = 0; i < bottomLeftTopRightIdx.length; i++) {
            if (howManyConsecutiveCounter === 4) {
                return this.state.squares[bottomLeftTopRightIdx[i]]; // winner has been found, return the winner
            } else if (this.state.squares[bottomLeftTopRightIdx[i]] !== null && this.state.squares[bottomLeftTopRightIdx[i]] === this.state.squares[bottomLeftTopRightIdx[i + 1]]) {
                howManyConsecutiveCounter++;
            } else {
                // either the loop ends and there is no winner found here OR the next adjacent piece is a different color
                howManyConsecutiveCounter = 1;
            }
        }

        // NOW DO THE OTHER DIAGONAL... BOTTOM-RIGHT TO TOP-LEFT relative to the this.state.selectedSquareIdx
        // get bottom right squares as indices
        const bottomRightTopLeftIdx = [];
        i = 1;
        while (this.state.selectedSquareIdx - 7 * i >= 0) {
            if (!leftColumnIdx.has(this.state.selectedSquareIdx - 7 * i + i)) {
                bottomRightTopLeftIdx.unshift(this.state.selectedSquareIdx - 7 * i + i);
                i++;
            } else {
                break;
            }
        }

        // push the selectedSquareIdx into the bottomRightTopLeftIdx array
        bottomRightTopLeftIdx.push(this.state.selectedSquareIdx);

        // get the top left squares as indices
        i = 1;
        while (this.state.selectedSquareIdx + 7 * i < 42) {
            if (!rightColumnIdx.has(this.state.selectedSquareIdx + 7 * i - i)) {
                bottomRightTopLeftIdx.push(this.state.selectedSquareIdx + 7 * i - i);
                i++;
            } else {
                break;
            }
        }

        // check to see if there is a 4-in-a-row from bottom right to top left
        howManyConsecutiveCounter = 1;
        for (let i = 0; i < bottomRightTopLeftIdx.length; i++) {
            if (howManyConsecutiveCounter === 4) {
                return this.state.squares[bottomRightTopLeftIdx[i]]; // winner has been found, return the winner
            } else if (this.state.squares[bottomRightTopLeftIdx[i]] !== null && this.state.squares[bottomRightTopLeftIdx[i]] === this.state.squares[bottomRightTopLeftIdx[i + 1]]) {
                howManyConsecutiveCounter++;
            } else {
                // either the loop ends and there is no winner found here OR the next adjacent piece is a different color
                howManyConsecutiveCounter = 1;
            }
        }

        return null;
    }

    render() {

        // calculate the winner
        const winner = this.calculateWinner();

        // let the AI make a move if it's the AI's turn and the game isn't over
        if (winner === null && !this.state.isHumanTurn && this.state.squares.includes(null)) {
            let max = 6;
            let min = 0;
            let randIdx = Math.floor(Math.random() * (max - min + 1) + min);
            let aiSelectedIdx = this.findLegalSquare(randIdx);
            while (aiSelectedIdx === -1) {
                randIdx = Math.floor(Math.random() * (max - min + 1) + min);
                aiSelectedIdx = this.findLegalSquare(randIdx);
            }
            const squares = this.state.squares.slice();
            squares[aiSelectedIdx] = AI;
            this.setState(prevState => ({
                squares : squares,
                isHumanTurn: !prevState.isHumanTurn,
                selectedSquareIdx: aiSelectedIdx
            }));
        }

        // setup the display

        const horizontalRule = <hr style={{visibility: "hidden", marginBottom: "0"}} />;
        const button = <button style={{}} onClick={this.handleReset}>Reset</button>;

        let display =   <>
                            <h5 className="d-flex justify-content-center" >Good luck!</h5>
                            <div style={{textAlign: "center"}}>
                                {button}
                                {horizontalRule}
                            </div>
                            <div className="d-flex justify-content-center"><Board squares={this.state.squares} onClick={this.handleClick} /></div>
                        </>;
        if (winner !== null) {
            display =   <>
                            {winner === HUMAN ? <h5 className="d-flex justify-content-center">Congratulations! You won!</h5> : <h5 className="d-flex justify-content-center">The AI won! Try again!</h5>}
                            <div style={{textAlign: "center"}}>
                                {button}
                                {horizontalRule}
                            </div>
                            <div className="d-flex justify-content-center"><Board squares={this.state.squares} onClick={() => {return; /* this does nothing... Disables onClick */}} /></div>
                        </>;
        }

        return (
            <div className="container">
                <h2 className="d-flex justify-content-center">Welcome to Connect 4!</h2>
                {display}
            </div>
        );
    };
};

export default Connect4Game;
