import Square from './Square';
import React, { Component } from 'react';

class Board extends React.Component {
    renderSquare(rowIdx, colIdx) {
        return (
          <Square
            value={this.props.squares[rowIdx][colIdx]}
            onClick={() => this.props.onClick(rowIdx, colIdx)}
          />
        );
    }

    render() {

        const display = [];

        this.props.squares.map((rowOfSquares, rowIdx) => {
            display.push(
                <div className="board-row">
                    {rowOfSquares.map((square, colIdx) => {
                        this.renderSquare(rowIdx, colIdx);
                    })}                
                </div>
            );
        })

        return (
            {display}
        );
    }
}

export default Board;
