import Square from './Square';
import React, { Component } from 'react';

class Board extends React.Component {
    renderSquare(rowIdx, colIdx) {
        return (
          <Square
            value={this.props.squares[rowIdx][colIdx]}
            onClick={() => this.props.onClick(rowIdx, colIdx)}
          />
        );
    }

    render() {

        const display = [];

        for (let rowIdx = 0; rowIdx < this.props.squares.length; rowIdx++) {
            const rowOfSquares = [];
            for (let colIdx = 0; colIdx < this.props.squares[0].length; colIdx++) {
                rowOfSquares.push(this.renderSquare(rowIdx, colIdx));
            }
            display.push(
                <div className="board-row">
                    {rowOfSquares}
                </div>
            );
        }

        return (
            {display}
        );
    }
}

export default Board;
