import Square from './Square';
import React, { Component } from 'react';

class Board extends React.Component {
    renderSquare(rowIdx, colIdx) {
        return (
          <Square 
            key={(rowIdx + 1) * (colIdx + 1)}
            value={this.props.squares[rowIdx][colIdx]}
            onClick={() => this.props.onClick(rowIdx, colIdx)}
          />
        );
    }

    render() {

        const display = [];

        for (let rowIdx = 0; rowIdx < this.props.squares.length; rowIdx++) {
            const rowOfSquares = [];
            for (let colIdx = 0; colIdx < this.props.squares[0].length; colIdx++) {
                rowOfSquares.push(this.renderSquare(rowIdx, colIdx));
            }
            display.push(rowOfSquares);
        }

        return (
            <div>
                {display.map(rowOfSquares => {
                    <div className="board-row">
                        1
                        {rowOfSquares}
                    </div>
                })}
            </div>
        );
    }
}

export default Board;
