function findLegalSquare(board, idx) {
    /**
     * Returns the index of a legal square that can be filled.
     * Note the legal square will be within the same column as idx.
     * If all the squares within idx's column are filled, then this function returns -1.
     */
    const didNotFindLegalSquareIdx = -1;
    let traversingIdx = idx;
    while (traversingIdx - 7 >= 0) {
        traversingIdx -= 7;
    }
    for (traversingIdx; traversingIdx < 42; traversingIdx += 7) {
        if (board[traversingIdx] === null) {
            return traversingIdx;
        }
    }
    return didNotFindLegalSquareIdx;
}

function calculateScoreForMove(board, player, idx) {
    /**
     * Returns the score for placing player's piece onto the board whose location is idx.
     */

    let score = 0;
    const WINDOWLENGTH = 4;

    // score for center piece


    // Score for horizontal
    const frontOfRowIdx = idx - (idx % 7);
    const rowArr = board.slice(frontOfRowIdx, frontOfRowIdx + 7);
    rowArr[idx] = player;
    for (let frontIdx = 0; frontIdx < WINDOWLENGTH; frontIdx++) {
        const windowArr = rowArr.slice(frontIdx, frontIdx + WINDOWLENGTH);
        if (windowArr.filter(val => val === player).length === 4) {
            score += 100;
        } else if (windowArr.filter(val => val === player).length === 3 && windowArr.filter(val => val === null).length === 1) {
            score += 5;
        }
    }

    return score;
}

function calculateWinner(board, lastSquareIdx) {
    /**
     * Returns the name of the winner HUMAN or AI. Else returns null.
     */

    // Check horizontal
    const frontOfCurrRowIdx = lastSquareIdx - (lastSquareIdx % 7);
    let currRowIdx = frontOfCurrRowIdx;
    let howManyConsecutiveCounter = 1; // always starts at 1 because each piece counts as one consecutive piece
    for (currRowIdx; currRowIdx < frontOfCurrRowIdx + 7; currRowIdx++) {
        if (howManyConsecutiveCounter === 4) {
            return board[currRowIdx]; // winner has been found, return the winner
        } else if (currRowIdx + 1 < frontOfCurrRowIdx + 7 && board[currRowIdx] !== null && board[currRowIdx] === board[currRowIdx + 1]) {
            howManyConsecutiveCounter++;
        } else {
            // either the loop ends and there is no winner found here OR the next adjacent piece is a different color
            howManyConsecutiveCounter = 1;
        }
    }

    // Check vertical
    let currColIdx = lastSquareIdx % 7;
    const outOfBoundsIdx = 42;
    howManyConsecutiveCounter = 1; // always starts at 1 because each piece counts as one consecutive piece
    for (currColIdx; currColIdx < outOfBoundsIdx ; currColIdx += 7) {
        if (howManyConsecutiveCounter === 4) {
            return board[currColIdx]; // winner has been found, return the winner
        } else if (currColIdx + 7 < outOfBoundsIdx && board[currColIdx] !== null && board[currColIdx] === board[currColIdx + 7]) {
            howManyConsecutiveCounter++;
        } else {
            // either the loop ends and there is no winner found here OR the next adjacent piece is a different color
            howManyConsecutiveCounter = 1;
        }
    }

    // Check Diagonal

    // start with BOTTOM-LEFT TO TOP-RIGHT relative to the lastSquareIdx
    // get bottom left squares as indices
    const rightColumnIdx = new Set([6, 13, 20, 27, 34, 41]);
    const leftColumnIdx = new Set([0, 7, 14, 21, 28, 35]);
    const bottomLeftTopRightIdx = [];
    let i = 1;
    while (lastSquareIdx - 7 * i >= 0) {
        if (lastSquareIdx - 7 * i - i >= 0 && !rightColumnIdx.has(lastSquareIdx - 7 * i - i)) {
            bottomLeftTopRightIdx.unshift(lastSquareIdx - 7 * i - i);
            i++;
        } else {
            break;
        }
    }

    // push the selectedSquareIdx into the bottomLeftTopRightIdx array
    bottomLeftTopRightIdx.push(lastSquareIdx);

    // get the top right squares as indices
    i = 1;
    while (lastSquareIdx + 7 * i < 42) {
        if (lastSquareIdx + 7 * i + i < 42 && !leftColumnIdx.has(lastSquareIdx + 7 * i + i)) {
            bottomLeftTopRightIdx.push(lastSquareIdx + 7 * i + i);
            i++;
        } else {
            break;
        }
    }

    // check to see if there is a 4-in-a-row from bottom left to top right
    howManyConsecutiveCounter = 1;
    for (let i = 0; i < bottomLeftTopRightIdx.length; i++) {
        if (howManyConsecutiveCounter === 4) {
            return board[bottomLeftTopRightIdx[i]]; // winner has been found, return the winner
        } else if (board[bottomLeftTopRightIdx[i]] !== null && board[bottomLeftTopRightIdx[i]] === board[bottomLeftTopRightIdx[i + 1]]) {
            howManyConsecutiveCounter++;
        } else {
            // either the loop ends and there is no winner found here OR the next adjacent piece is a different color
            howManyConsecutiveCounter = 1;
        }
    }

    // NOW DO THE OTHER DIAGONAL... BOTTOM-RIGHT TO TOP-LEFT relative to the lastSquareIdx
    // get bottom right squares as indices
    const bottomRightTopLeftIdx = [];
    i = 1;
    while (lastSquareIdx - 7 * i >= 0) {
        if (!leftColumnIdx.has(lastSquareIdx - 7 * i + i)) {
            bottomRightTopLeftIdx.unshift(lastSquareIdx - 7 * i + i);
            i++;
        } else {
            break;
        }
    }

    // push the selectedSquareIdx into the bottomRightTopLeftIdx array
    bottomRightTopLeftIdx.push(lastSquareIdx);

    // get the top left squares as indices
    i = 1;
    while (lastSquareIdx + 7 * i < 42) {
        if (!rightColumnIdx.has(lastSquareIdx + 7 * i - i)) {
            bottomRightTopLeftIdx.push(lastSquareIdx + 7 * i - i);
            i++;
        } else {
            break;
        }
    }

    // check to see if there is a 4-in-a-row from bottom right to top left
    howManyConsecutiveCounter = 1;
    for (let i = 0; i < bottomRightTopLeftIdx.length; i++) {
        if (howManyConsecutiveCounter === 4) {
            return board[bottomRightTopLeftIdx[i]]; // winner has been found, return the winner
        } else if (board[bottomRightTopLeftIdx[i]] !== null && board[bottomRightTopLeftIdx[i]] === board[bottomRightTopLeftIdx[i + 1]]) {
            howManyConsecutiveCounter++;
        } else {
            // either the loop ends and there is no winner found here OR the next adjacent piece is a different color
            howManyConsecutiveCounter = 1;
        }
    }

    return null;
}

function idxOfMaxOfArr(arr) {
    let max = arr[0];
    let maxIdx = 0;
    for (let i = 0; i < arr.length; i++) {
        if (arr[i] > max) {
            max = arr[i]
            maxIdx = i;
        }
    }
    return maxIdx;
}

function minimax(board, depth, maximizingPlayer) {
    /**
     * minimax algorithm to determine how the ai will make its move
     * refer to the pseudocode on wikipedia
     */

    // get all the possible moves that the player can drop into
    // this should be an array with the indices
    const possibleMovesArr = [0,1,2,3,4,5,6].map(idx => findLegalSquare(board, idx)).filter(idx => idx !== -1);
    const scoresForPossibleMovesArr = possibleMovesArr.map(idx => calculateScoreForMove(board, maximizingPlayer, idx));

    return possibleMovesArr[idxOfMaxOfArr(scoresForPossibleMovesArr)];
}

export default minimax;
