const TOTALNUMOFCOLUMNS = 7;
const TOTALNUMOFROWS = 6;
const TOTALNUMOFSQUARES = 42;

function findLegalRowIdxWithinColumn(board, selectedColIdx) {
    /**
     * Returns the row index of a legal square that can be filled given the board (2 dimensional row x col).
     * Note the legal square will be within the same column as selectedColIdx.
     * If all the squares within selectedColIdx's column are filled, then this function returns -1.
     */
    const didNotFindLegalRowIdxWithinColumnIdx = -1;
    for (let rowIdx = 0; rowIdx < board.length; rowIdx++) {
        if (board[rowIdx][selectedColIdx] === null) {
            return rowIdx;
        }
    }
    return didNotFindLegalRowIdxWithinColumnIdx;
}

function calculateScoreForThese4Squares(arr, player, opposingPlayer) {
    /**
     * Returns the score for the given arr, assuming the length of the arr is 4
     */

    let score = 0;

    if (arr.filter(val => val === player).length === 4) {
        score += 100;
    } else if (arr.filter(val => val === player).length === 3 && arr.filter(val => val === null).length === 1) {
        score += 5;
    } else if (arr.filter(val => val === player).length === 2 && arr.filter(val => val === null).length === 2) {
        score += 2;
    }

    if (arr.filter(val => val === opposingPlayer).length === 3 && arr.filter(val => val === null).length === 1) {
        score -= 15;
    }

    return score;
}

function calculateScoreForMove(board, idx, player, opposingPlayer) {
    /**
     * Returns the score for placing player's piece onto the board whose location is idx.
     */

    let score = 0;
    const WINDOWLENGTH = 4;
    const CENTERCOLUMNIDX = Math.floor(TOTALNUMOFCOLUMNS / 2);
    const newBoard = board.slice();
    newBoard[idx] = player;

    // score for center piece (preference center location)
    for (let i = CENTERCOLUMNIDX; i < TOTALNUMOFSQUARES; i += TOTALNUMOFCOLUMNS) {
        if (newBoard[i] === player) {
            score += 2;
        }
    }


    // Score for horizontal
    // Scores are calculated for window lengths of 4
    for (let currRowIdx = 0; currRowIdx < TOTALNUMOFSQUARES; currRowIdx += TOTALNUMOFCOLUMNS) {
        for (let offset = 0; offset <= TOTALNUMOFCOLUMNS - WINDOWLENGTH; offset++) {
            const horizontalFourAdjacentSquares = newBoard.slice(currRowIdx + offset, currRowIdx + offset + WINDOWLENGTH);
            score += calculateScoreForThese4Squares(horizontalFourAdjacentSquares, player, opposingPlayer);
        }
    }

    // Score for vertical
    // Scores are calculated for window lengths of 4
    for (let currColIdx = 0; currColIdx < TOTALNUMOFCOLUMNS; currColIdx++) {
        const aColumnOfSquaresArr = [];
        for (let i = currColIdx; i < TOTALNUMOFSQUARES; i += TOTALNUMOFCOLUMNS) { // gets the stack of squares in the given column denoted by currColIdx
            aColumnOfSquaresArr.push(newBoard[i]);
        }
        for (let i = 0; i <= TOTALNUMOFROWS - WINDOWLENGTH; i++) {
            const verticalFourAdjacentSquares = aColumnOfSquaresArr.slice(i, i + WINDOWLENGTH);
            score += calculateScoreForThese4Squares(verticalFourAdjacentSquares, player, opposingPlayer);
        }

    }

    // Score for diagonal
    // Scores are calculated for window lengths of 4

    // Calculate score for positive sloping diagonal (bottom-left to top-right FOR THE LEFT HALF OF THE BOARD)
    for (let currIdx = TOTALNUMOFCOLUMNS * (Math.floor(TOTALNUMOFROWS / 2) - 1); currIdx >= 0; currIdx -= TOTALNUMOFCOLUMNS) {
        const aDiagonalOfSquaresArr = [];
        // assemble the diagonal of squares arr for the given currIdx
        for (let i = 0; currIdx + i * TOTALNUMOFCOLUMNS + i < TOTALNUMOFSQUARES; i++) {
            aDiagonalOfSquaresArr.push(newBoard[currIdx + i * TOTALNUMOFCOLUMNS + i]);
        }
        // slice the diagonal squares array with window lengths of 4 and calculate score
        for (let i = 0; i <= aDiagonalOfSquaresArr.length - WINDOWLENGTH; i++) {
            const diagonalFourAdjacentSquares = aDiagonalOfSquaresArr.slice(i, i + WINDOWLENGTH);
            score += calculateScoreForThese4Squares(diagonalFourAdjacentSquares, player, opposingPlayer);
        }
    }

    // Continue calculating the score for positive sloping diagonal (bottom-left to top-right FOR THE RIGHT HALF OF THE BOARD)
    for (let currIdx = (TOTALNUMOFCOLUMNS * Math.floor(TOTALNUMOFROWS / 2)) + TOTALNUMOFCOLUMNS - 1; currIdx < TOTALNUMOFSQUARES; currIdx += TOTALNUMOFCOLUMNS) {
        const aDiagonalOfSquaresArr = [];
        // assemble the diagonal of squares arr for the given currIdx
        for (let i = 0; currIdx - i * TOTALNUMOFCOLUMNS - i >= 0; i++) {
            aDiagonalOfSquaresArr.push(newBoard[currIdx - i * TOTALNUMOFCOLUMNS - i]);
        }
        // slice the diagonal squares array with window lengths of 4 and calculate score
        for (let i = 0; i <= aDiagonalOfSquaresArr.length - WINDOWLENGTH; i++) {
            const diagonalFourAdjacentSquares = aDiagonalOfSquaresArr.slice(i, i + WINDOWLENGTH);
            score += calculateScoreForThese4Squares(diagonalFourAdjacentSquares, player, opposingPlayer);
        }
    }

    // Calculate score for negative sloping diagonal (top-left to bottom-right FOR THE LEFT HALF OF THE BOARD)
    for (let currIdx = TOTALNUMOFCOLUMNS * Math.floor(TOTALNUMOFROWS / 2); currIdx < TOTALNUMOFSQUARES; currIdx += TOTALNUMOFCOLUMNS) {
        const aDiagonalOfSquaresArr = [];
        // assemble the diagonal of squares arr for the given currIdx
        for (let i = 0; currIdx - i * TOTALNUMOFCOLUMNS + i >= 0; i++) {
            aDiagonalOfSquaresArr.push(newBoard[currIdx - i * TOTALNUMOFCOLUMNS + i]);
        }
        // slice the diagonal squares array with window lengths of 4 and calculate score
        for (let i = 0; i <= aDiagonalOfSquaresArr.length - WINDOWLENGTH; i++) {
            const diagonalFourAdjacentSquares = aDiagonalOfSquaresArr.slice(i, i + WINDOWLENGTH);
            score += calculateScoreForThese4Squares(diagonalFourAdjacentSquares, player, opposingPlayer);
        }
    }

    // CONTINUE HERE AFTER DINNER

    // Continue calculating the score for negative sloping diagonal (top-left to bottom-right FOR THE RIGHT HALF OF THE BOARD)
    for (let currRowIdx = TOTALNUMOFCOLUMNS * (Math.floor(TOTALNUMOFROWS / 2) - 1); currRowIdx < TOTALNUMOFSQUARES; currRowIdx += TOTALNUMOFCOLUMNS) {
        const aDiagonalOfSquaresArr = [];
        // assemble the diagonal of squares arr for the given currRowIdx
        for (let i = 0; currRowIdx - i * TOTALNUMOFCOLUMNS + i >= 0; i++) {
            aDiagonalOfSquaresArr.push(newBoard[currRowIdx - i * TOTALNUMOFCOLUMNS + i]);
        }
        // slice the diagonal squares array with window lengths of 4 and calculate score
        for (let i = 0; i <= aDiagonalOfSquaresArr.length - WINDOWLENGTH; i++) {
            const diagonalFourAdjacentSquares = aDiagonalOfSquaresArr.slice(i, i + WINDOWLENGTH);
            score += calculateScoreForThese4Squares(diagonalFourAdjacentSquares, player, opposingPlayer);
        }
    }

    return score;
}

