import Square from './Square';
import React, { Component } from 'react';

class Board extends React.Component {
    renderSquare(rowIdx, colIdx) {
        return (
          <Square
            value={this.props.squares[rowIdx][colIdx]}
            onClick={() => this.props.onClick(rowIdx, colIdx)}
          />
        );
    }

    render() {
        const row5 = [];
        for (let i = 0; i < 7; i++) {
            row5.push(this.renderSquare(i));
        }

        const row4 = [];
        for (let i = 7; i < 14; i++) {
            row4.push(this.renderSquare(i));
        }

        const row3 = [];
        for (let i = 14; i < 21; i++) {
            row3.push(this.renderSquare(i));
        }

        const row2 = [];
        for (let i = 21; i < 28; i++) {
            row2.push(this.renderSquare(i));
        }

        const row1 = [];
        for (let i = 28; i < 35; i++) {
            row1.push(this.renderSquare(i));
        }

        const row0 = [];
        for(let i = 35; i < 42; i++) {
            row0.push(this.renderSquare(i));
        }

        for (let rowIdx = 0; rowIdx < this.props.squares.length; rowIdx++) {
            this.props.squares[rowIdx];
        }

        const display = [];

        this.props.squares.map((rowOfSquares, rowIdx) => {
            display.push(
                <div className="board-row">
                    {rowOfSquares.map((square, colIdx) => {
                        this.renderSquare(rowIdx, colIdx);
                    })}                
                </div>
            );
        })
        return (
            {display}
        );
    }
}

export default Board;
